#!/bin/bash
# Create placeholder silent MP3 files (1 second of silence)
# This is a minimal valid MP3 file header + silent frame

# Base64 encoded minimal MP3 (short silence ~0.5s)
SILENT_MP3_B64=""

# Create all voice MP3 files
for voice in alloy ash ballad coral echo sage shimmer verse marin; do
    echo "$SILENT_MP3_B64" | base64 -d > "${voice}.mp3"
    echo "Created ${voice}.mp3"
done

echo "All placeholder MP3 files created!"
